const callFormSubmit = () => {

  const $form = $('.call__form');
  let $error = false;

  $('.call__form input[required]').each(function () {
    if ($(this).val() == '') {
      $(this).addClass('error');
      $(this)[0].focus();

      console.log(`error: ` + $(this).attr('placeholder'));
      $error = true
    }
  });

  if (!$error) {
    const $data = $form.serialize();

    $.ajax({
      type: 'POST',
      url: 'send.php',
      data: $data,
      error: function(xhr, ajaxOptions, thrownError) {
        console.log(xhr.status);
        console.log(thrownError)
      },
      success: function(data) {
        if (data['error']) {
          console.log(`error: ${data['error']}`)
        } else {
          // showFormOverlay();
          sendRequestModal.classList.remove('modal--hidden');
          sendRequestModal.classList.add('modal--visible');
          closeBtn.click();
          modalRequestTimeout();

          setTimeout(() => {
            $('.call__form')[0].reset();
            $('.call__input').focusout()
          }, 400)
        }
      }
    })
  }

  return false
};

$('.call__form').submit(callFormSubmit);

const openBtn = document.querySelector('#callBtn');
const closeBtn = document.querySelector('.call__close');
const callModal = document.querySelector('.call');
const callSubmitBtn = document.querySelector('#callSubmit');

const sendRequestModal = document.querySelector('#requestSend');
const closeModalRequestBtn = document.querySelector('#requestModalClose');

closeBtn.addEventListener('click', () => {
  callModal.classList.add('call--closed');
  callModal.classList.remove('call--opened');
  clearTimeout(modalRequestTimeout);
});

openBtn.addEventListener('click', () => {
  callModal.classList.add('call--opened');
  callModal.classList.remove('call--closed');
});

// callSubmitBtn.addEventListener('click', (event) => {
//   event.preventDefault();
//   sendMessageModal.classList.add('modal--visible');
//   sendMessageModal.classList.remove('modal--hidden');
//   closeBtn.click();
// });

closeModalRequestBtn.addEventListener('click', () => {
  sendRequestModal.classList.remove('modal--visible');
  sendRequestModal.classList.add('modal--hidden');
});

function modalRequestTimeout() {
  const timeout = setTimeout(() => {
    sendRequestModal.classList.remove('modal--visible');
    sendRequestModal.classList.add('modal--hidden');
  }, 5000);
}


const intro = document.querySelector('.intro');
const messages = document.querySelectorAll('.intro__message');
const messagesTexts = document.querySelectorAll('.intro__text');
let isMobile = navigator.userAgent.match(/Android/i)
    || navigator.userAgent.match(/webOS/i)
    || navigator.userAgent.match(/iPhone/i)
    || navigator.userAgent.match(/iPad/i)
    || navigator.userAgent.match(/iPod/i)
    || navigator.userAgent.match(/BlackBerry/i)
    || navigator.userAgent.match(/Windows Phone/i);

if (isMobile) {
  [].slice.call(messagesTexts).forEach((message) => {
    message.style.visibility = 'hidden';
  });

  setTimeout(() => {
    messagesTexts[0].style.visibility = 'visible'
  }, 2100);

  setTimeout(() => {
    messagesTexts[1].style.visibility = 'visible'
  }, 4200);

  setTimeout(() => {
    messagesTexts[2].style.visibility = 'visible'
  }, 6300);
}

if (!isMobile) {
  [].slice.call(messagesTexts).forEach((message) => {
    message.style.visibility = 'visible';
  });
  // messages[1].style.top = `${intro.offsetHeight / 4}px`;
  // messages[1].style.left = `${intro.offsetWidth / 5}px`;
  // messages[2].style.top = `${intro.offsetHeight / 2.5}px`;
  // messages[2].style.right = `${intro.offsetWidth / 8}px`;
}



const formSubmit = () => {

  const $form = $('.contacts__form');
  let $error = false;

  $('.contacts__form input[required]').each(function () {
    if ($(this).val() == '') {
      $(this).addClass('error');
      $(this)[0].focus();

      console.log(`error: ` + $(this).attr('placeholder'));
      $error = true
    }
  });

  if (!$error) {
    const $data = $form.serialize();

    $.ajax({
      type: 'POST',
      url: 'send.php',
      data: $data,
      error: function(xhr, ajaxOptions, thrownError) {
        console.log(xhr.status);
        console.log(thrownError)
      },
      success: function(data) {
        if (data['error']) {
          console.log(`error: ${data['error']}`)
        } else {
          // showFormOverlay();
          sendMessageModal.classList.add('modal--visible');
          sendMessageModal.classList.remove('modal--hidden');
          modalMessageTimeout();

          setTimeout(() => {
            $('.contacts__form')[0].reset();
            $('.contacts__input, .contacts__message').focusout()
          }, 400)
        }
      }
    })
  }

  return false
};

$('.contacts__form').submit(formSubmit);

